* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
  scrollbar-width: 40px;
}

body {
  font-size: 1.6rem;
  font-family: "Hind";
  color: #252525;
  position: relative;
}

.btn {
  text-decoration: none;
  padding: 1.2rem 2rem 0.9rem 2rem;
  background-color: white;
  display: flex;
  align-items: center;
  line-height: 1;
  font-size: 1.9rem;
  font-weight: 400;
  transition: all 0.2s;
}
.btn__normal {
  border-radius: 100rem;
}
.btn__normal:hover {
  color: #7510fa;
}
.btn__fill-on-hover {
  border-radius: 100rem;
  border: 2px solid #7510fa;
  transform: translateY(-2px);
  color: #7510fa;
}
.btn__fill-on-hover:hover {
  background-color: #7510fa;
  color: white;
}
.btn__intro {
  background-color: rgba(93, 4, 211, 0.45);
  padding: 2rem 4rem;
  color: white;
  display: inline-block;
  border-radius: 999rem;
  font-size: 2.2rem;
}
.btn__intro:hover {
  background-color: #5d04d3;
}

.main-icon {
  display: flex;
  width: 6rem;
  height: 6rem;
  align-items: center;
  justify-content: center;
}
.main-icon img {
  display: inline-block;
  height: 100%;
  width: 100%;
  transform: scaleY(0.92);
}

.work-figure {
  transition: all 0.3s;
  position: relative;
  overflow: hidden;
  border-radius: 1.5rem;
}
.work-figure__start {
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
}
.work-figure__img {
  transition: all 0.3s;
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 1.5rem;
}
.work-figure:hover {
  box-shadow: 0px 5px 17px 7px rgba(0, 0, 0, 0.1);
}
.work-figure:hover .work-figure__img {
  transform: scale(1.03);
}
.work-figure:hover .work-figure__hovered {
  opacity: 1;
  visibility: visible;
}
.work-figure__figcaption {
  font-size: calc(1.4vw + 2rem);
  color: #e9e9e9;
  text-align: center;
  letter-spacing: 2px;
  line-height: 1;
  background-color: whitesmoke;
  position: absolute;
  display: inline-block;
  left: 0;
  width: 100%;
  z-index: 22;
}
.work-figure__hovered {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  background-color: rgba(36, 37, 42, 0.85);
  z-index: 10;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 2rem;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s;
}
.work-figure__title {
  width: 100%;
  display: inline-block;
  font-size: calc(1vw + 1.6rem);
  font-weight: #424242;
  color: #e9e9e9;
  text-align: center;
  text-transform: uppercase;
  letter-spacing: 1px;
}
.work-figure__description {
  color: #e9e9e9;
  text-align: center;
  font-size: 1.8rem;
}
.work-figure__buttons-wrapper {
  display: flex;
  justify-content: space-around;
}
.work-figure__button {
  text-decoration: none;
  display: flex;
  justify-content: center;
  padding: 1rem 2rem;
  border: none;
  color: #e9e9e9;
  font-size: 2.1rem;
  border-radius: 1000px;
  white-space: nowrap;
  background-color: crimson;
  transition: all 0.15s;
}
.work-figure__button:hover {
  background-color: #c01134;
}

.nav {
  display: flex;
  justify-content: space-between;
  align-content: center;
  color: white;
  padding: 0.7rem 1.6rem;
  background-color: #a05bfc;
}
.nav div {
  grid-column: 2/3;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.nav__buttons {
  list-style: none;
  display: flex;
  align-items: center;
}
.nav__button {
  height: 100%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 1rem;
}
@media only screen and (max-width: 450px) {
  .nav__button {
    padding: 0 0.4rem;
  }
}
.nav__button-link {
  color: inherit;
  list-style: none;
  text-decoration: none;
  font-size: 1.9rem;
  font-weight: 500;
  background-color: transparent;
  outline: none;
  border: none;
  cursor: inherit;
}
@media only screen and (max-width: 450px) {
  .nav__button-link {
    font-size: 1.6rem;
  }
}
.nav__image {
  height: 6rem;
  width: 6rem;
}

.intro {
  color: #222;
  padding: calc(8vw - 2rem) 2rem;
  padding-top: 8vw;
  height: calc(16rem + 12vh);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.intro__title {
  margin-bottom: 2.4rem;
}

.work {
  display: grid;
  grid-template-columns: [work-full-start] 1fr [work-full-end];
  grid-template-rows: 10rem;
  gap: 3rem;
  padding: 0 calc(10vw - 3rem) 12rem calc(10vw - 3rem);
}
.work__title {
  grid-column: work-full-start/work-full-end;
  grid-row: 1/2;
  text-align: center;
  align-self: center;
}
.work__main {
  display: grid;
  grid-column: work-full-start/work-full-end;
  grid-auto-rows: minmax(28rem, 28rem);
  grid-template-columns: repeat(auto-fit, minmax(calc(27rem + 11vw), 1fr));
  gap: 6rem;
}

.skills {
  display: flex;
  width: 100%;
  align-items: center;
  justify-content: center;
  padding: 2rem 0;
}
.skills__image {
  height: calc(1.5vw + 4rem);
  margin: 0 calc(1vw + 0.4rem);
}

.contact {
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 4rem;
}

.contact__title {
  font-weight: 500;
  font-size: 3.4rem;
  letter-spacing: 1px;
  margin-bottom: 3rem;
}

.contact__form {
  display: flex;
  flex-direction: column;
  align-items: center;
}

.contact__form-item {
  color: white;
  background-color: #3a3a3a;
  border: none;
  outline: none;
  border-radius: 0.5rem;
  font-size: 2rem;
  padding: 1rem;
}

.contact__email {
  height: 4rem;
  font-size: 2rem;
  font-family: monospace;
  margin-bottom: 2rem;
  width: calc(50vw + 14rem);
}

.contact__message {
  height: 23rem;
  margin-bottom: 2rem;
  width: calc(50vw + 14rem);
}

.contact__btn {
  border-radius: 0.5rem;
  font-family: monospace;
  padding: 0.8rem 1.3rem;
  font-size: 2.4rem;
  background-color: #3a3a3a;
  color: #eee;
  border: none;
  outline: none;
  cursor: pointer;
}

